class PropertySeeder extends Seeder
{
    /**
     * Run the database seeds.
     */
    public function run(): void
    {
        $propertyManager = $this->getPropertyManager();

        // One property per type/subtype combination - fully hardcoded
        $properties = $this->getHardcodedProperties();

        foreach ($properties as $index => $propertyData) {
            $property = Property::create(array_merge($propertyData, [
                'property_manager_id' => $propertyManager->id,
            ]));

            // Load images if folder exists
            $this->loadPropertyImages($property, $index + 1);

            $this->command->info("Created: {$property->title}");
        }

        $this->command->info("\nCreated ".count($properties)." properties \n");
    }

    /**
     * Get the property manager for seeding.
     */
    private function getPropertyManager(): PropertyManager
    {
        $user = User::where('email', 'professional@test.com')->first();

        if (! $user) {
            $this->command->error('Professional user not found. Run TestUserSeeder first.');
            exit(1);
        }

        return $user->propertyManager;
    }

