    /**
     * Load property images from the seeders/images directory.
     */
    private function loadPropertyImages(Property $property, int $propertyNumber): void
    {
        $imagesPath = database_path("seeders/images/property-{$propertyNumber}");

        if (! File::exists($imagesPath)) {
            return;
        }

        $imageFiles = array_merge(
            File::glob($imagesPath.'/*.jpg'),
            File::glob($imagesPath.'/*.jpeg'),
            File::glob($imagesPath.'/*.png'),
            File::glob($imagesPath.'/*.webp')
        );

        if (empty($imageFiles)) {
            return;
        }

        sort($imageFiles);

        foreach ($imageFiles as $index => $imagePath) {
            $fileName = basename($imagePath);
            $isMain = ($index === 0);

            $disk = StorageHelper::getDisk('private');
            $fileContents = File::get($imagePath);
            $storedPath = "properties/{$property->id}/{$fileName}";

            \Illuminate\Support\Facades\Storage::disk($disk)->put($storedPath, $fileContents);

            PropertyImage::create([
                'property_id' => $property->id,
                'image_path' => $storedPath,
                'sort_order' => $index,
                'is_main' => $isMain,
            ]);
        }
    }
